import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ActivityLogs = () => {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [filter, setFilter] = useState('all'); // all, login, registration

  useEffect(() => {
    fetchActivityLogs();
  }, []);

  const fetchActivityLogs = async () => {
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: { Authorization: `Bearer ${token}` }
      };

      const response = await axios.get('/api/admin/activity-logs', config);
      setLogs(response.data.logs);
    } catch (err) {
      console.error('Activity logs fetch error:', err);
      setError(err.response?.data?.message || 'Failed to fetch activity logs. Please ensure the backend server is running.');
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  const getActivityIcon = (activityType) => {
    switch (activityType) {
      case 'login':
        return 'ðŸ”';
      case 'registration':
        return 'ðŸ‘¤';
      default:
        return 'ðŸ“';
    }
  };

  const getActivityColor = (activityType) => {
    switch (activityType) {
      case 'login':
        return '#28a745';
      case 'registration':
        return '#007bff';
      default:
        return '#6c757d';
    }
  };

  const filteredLogs = logs.filter(log => {
    if (filter === 'all') return true;
    return log.activity_type === filter;
  });

  if (loading) {
    return <div className="loading">Loading activity logs...</div>;
  }

  if (error) {
    return <div className="error">Error: {error}</div>;
  }

  return (
    <div>
      <h1 style={{ marginBottom: '30px' }}>System Activity Logs</h1>

      {/* Filter Controls */}
      <div className="card" style={{ marginBottom: '20px' }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
          <label style={{ fontWeight: 'bold' }}>Filter by Activity:</label>
          <select 
            value={filter} 
            onChange={(e) => setFilter(e.target.value)}
            style={{ padding: '8px', borderRadius: '4px', border: '1px solid #ddd' }}
          >
            <option value="all">All Activities ({logs.length})</option>
            <option value="login">Logins ({logs.filter(log => log.activity_type === 'login').length})</option>
            <option value="registration">Registrations ({logs.filter(log => log.activity_type === 'registration').length})</option>
          </select>
        </div>
      </div>

      {/* Activity Logs */}
      <div className="card">
        <h2>Recent System Activity ({filteredLogs.length})</h2>
        {filteredLogs.length > 0 ? (
          <div style={{ overflowX: 'auto' }}>
            <table className="table">
              <thead>
                <tr>
                  <th>Time</th>
                  <th>Activity</th>
                  <th>User</th>
                  <th>IP Address</th>
                  <th>User Agent</th>
                </tr>
              </thead>
              <tbody>
                {filteredLogs.map((log, index) => (
                  <tr key={index}>
                    <td>{formatDate(log.timestamp)}</td>
                    <td>
                      <span style={{
                        display: 'flex',
                        alignItems: 'center',
                        gap: '8px',
                        padding: '4px 8px',
                        borderRadius: '4px',
                        fontSize: '12px',
                        fontWeight: 'bold',
                        backgroundColor: getActivityColor(log.activity_type),
                        color: 'white'
                      }}>
                        {getActivityIcon(log.activity_type)}
                        {log.activity_type.charAt(0).toUpperCase() + log.activity_type.slice(1)}
                      </span>
                    </td>
                    <td>
                      {log.id_number || log.email || 'N/A'}
                    </td>
                    <td>{log.ip_address || 'N/A'}</td>
                    <td style={{ maxWidth: '200px', overflow: 'hidden', textOverflow: 'ellipsis' }}>
                      {log.user_agent || 'N/A'}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <p style={{ textAlign: 'center', color: '#666', padding: '20px' }}>
            No activity logs found.
          </p>
        )}
      </div>

      {/* Activity Summary */}
      <div className="card" style={{ marginTop: '20px' }}>
        <h3>Activity Summary</h3>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '20px' }}>
          <div style={{ textAlign: 'center', padding: '15px', backgroundColor: '#f8f9fa', borderRadius: '8px' }}>
            <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#007bff' }}>
              {logs.filter(log => log.activity_type === 'registration').length}
            </div>
            <div>Total Registrations</div>
          </div>
          <div style={{ textAlign: 'center', padding: '15px', backgroundColor: '#f8f9fa', borderRadius: '8px' }}>
            <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#28a745' }}>
              {logs.filter(log => log.activity_type === 'login').length}
            </div>
            <div>Total Logins</div>
          </div>
          <div style={{ textAlign: 'center', padding: '15px', backgroundColor: '#f8f9fa', borderRadius: '8px' }}>
            <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#6c757d' }}>
              {logs.length}
            </div>
            <div>Total Activities</div>
          </div>
        </div>
      </div>

      <button 
        className="btn btn-secondary" 
        onClick={fetchActivityLogs}
        style={{ marginTop: '20px' }}
      >
        Refresh Logs
      </button>
    </div>
  );
};

export default ActivityLogs;
