import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserManagement = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: { Authorization: `Bearer ${token}` }
      };

      const response = await axios.get('/api/admin/users', config);
      setUsers(response.data.users);
    } catch (err) {
      console.error('User fetch error:', err);
      setError(err.response?.data?.message || 'Failed to fetch users. Please ensure the backend server is running.');
    } finally {
      setLoading(false);
    }
  };

  const updateUserVerification = async (idNumber, isVerified) => {
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: { Authorization: `Bearer ${token}` }
      };

      await axios.put(`/api/admin/users/${idNumber}/verify`, { isVerified }, config);
      alert(`User verification status updated to ${isVerified ? 'verified' : 'unverified'}`);
      fetchUsers(); // Refresh the list
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to update user verification');
    }
  };

  const deleteUser = async (idNumber) => {
    if (!window.confirm('Are you sure you want to delete this user? This action cannot be undone.')) {
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: { Authorization: `Bearer ${token}` }
      };

      await axios.delete(`/api/admin/users/${idNumber}`, config);
      alert('User deleted successfully');
      fetchUsers(); // Refresh the list
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to delete user');
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  if (loading) {
    return <div className="loading">Loading users...</div>;
  }

  if (error) {
    return <div className="error">Error: {error}</div>;
  }

  return (
    <div>
      <h1 style={{ marginBottom: '30px' }}>User Management</h1>

      {/* Users List */}
      <div className="card">
        <h2>All Users ({users.length})</h2>
        {users.length > 0 ? (
          <div style={{ overflowX: 'auto' }}>
            <table className="table">
              <thead>
                <tr>
                  <th>ID Number</th>
                  <th>Email</th>
                  <th>Status</th>
                  <th>Login Count</th>
                  <th>Created</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {users.map((user) => (
                  <tr key={user.id}>
                    <td>{user.id_number}</td>
                    <td>{user.email}</td>
                    <td>
                      <span style={{
                        padding: '4px 8px',
                        borderRadius: '4px',
                        fontSize: '12px',
                        fontWeight: 'bold',
                        backgroundColor: user.is_verified ? '#28a745' : '#dc3545',
                        color: 'white'
                      }}>
                        {user.is_verified ? 'Verified' : 'Unverified'}
                      </span>
                    </td>
                    <td>{user.login_count || 0}</td>
                    <td>{formatDate(user.created_at)}</td>
                    <td>
                      <div style={{ display: 'flex', gap: '5px' }}>
                        <button
                          className="btn btn-sm btn-warning"
                          onClick={() => updateUserVerification(user.id_number, !user.is_verified)}
                        >
                          {user.is_verified ? 'Unverify' : 'Verify'}
                        </button>
                        <button
                          className="btn btn-sm btn-danger"
                          onClick={() => deleteUser(user.id_number)}
                        >
                          Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <p style={{ textAlign: 'center', color: '#666', padding: '20px' }}>
            No users found.
          </p>
        )}
      </div>

      <button 
        className="btn btn-secondary" 
        onClick={fetchUsers}
        style={{ marginTop: '20px' }}
      >
        Refresh Users
      </button>
    </div>
  );
};

export default UserManagement;
